// Example of a flexible, modular system design
class SystemCore {
  constructor(modules = []) {
    this.modules = new Map();
    this.loadModules(modules);
  }

  loadModules(modules) {
    modules.forEach(module => {
      this.modules.set(module.name, module);
    });
  }

  executeWorkflow(workflowName, data) {
    const workflow = this.modules.get(workflowName);
    return workflow ? workflow.execute(data) : null;
  }
}

